            $total_price += $subtotal;
            
            $output .= '<tr>';
            $output .= '<td>'.$product['name'].'</td>';
            $output .= '<td>$'.$product['price'].'</td>';
            $output .= '<td>'.$quantity.'</td>';
            $output .= '<td>$'.number_format($subtotal, 2).'</td>';
            $output .= '</tr>';
        }
