<?php
class Product {
    private $id;
    private $name;
    private $price;
    private $description;
    private $stock;

    public function __construct($id, $name, $price, $description, $stock) {
        $this->id = $id;
        $this->name = $name;
        $this->price = $price;
        $this->description = $description;
        $this->stock = $stock;
    }

    public function getId() {
        return $this->id;
    }

    public function getName() {
        return $this->name;
    }

    public function getPrice() {
        return $this->price;
    }

    public function getDescription() {
        return $this->description;
    }

    public function getStock() {
        return $this->stock;
    }

    public static function getAllProducts($conn) {
        $stmt = $conn->prepare("SELECT * FROM products");
        $stmt->execute();
        $result = $stmt->get_result();
        
        $products = array();
        while ($row = $result->fetch_assoc()) {
            $product = new Product(
                $row['id'],
                $row['name'],
                $row['price'],
                $row['description'],
                $row['stock']
            );
            array_push($products, $product);
        }
        return $products;
    }

    public static function getProductById($conn, $id) {
        $stmt = $conn->prepare("SELECT * FROM products WHERE id = ?");
        $stmt->bind_param("i", $id);
        $stmt->execute();
        $result = $stmt->get_result();

        if ($row = $result->fetch_assoc()) {
            return new Product(
                $row['id'],
                $row['name'],
                $row['price'],
                $row['description'],
                $row['stock']
            );
        } else {
            return null;
        }
    }

    public function addToCart($conn, $userId) {
        // Add to cart logic
    }

    public static function removeFromCart($conn, $cartId) {
        // Remove from cart logic
    }

    public static function updateCartQuantity($conn, $cartId, $quantity) {
        // Update quantity in cart logic
    }
}
