<?php
session_start();

// Database configuration
$host = 'localhost';
$dbname = 'my_store';
$username = 'root';
$password = '';

try {
    $conn = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);
    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
} catch (PDOException $e) {
    echo "Connection failed: " . $e->getMessage();
}

// Add to cart function
function add_to_cart($product_id) {
    global $conn;
    
    // Check if product exists in database
    $stmt = $conn->prepare("SELECT * FROM products WHERE id = ?");
    $stmt->execute([$product_id]);
    $product = $stmt->fetch();
    
    if (!$product) {
        return false;
    }
    
    // Add to cart session
    $_SESSION['cart'][$product_id] = [
        'id' => $product_id,
        'name' => $product['name'],
        'price' => $product['price'],
        'quantity' => 1
    ];
    
    return true;
}

// Remove from cart function
function remove_from_cart($product_id) {
    if (isset($_SESSION['cart'][$product_id])) {
        unset($_SESSION['cart'][$product_id]);
        return true;
    }
    return false;
}

// Calculate total price function
function calculate_total() {
    $total = 0;
    foreach ($_SESSION['cart'] as $item) {
        $total += $item['price'] * $item['quantity'];
    }
    return $total;
}

// Checkout process function
function checkout($user_id, $address, $payment_method) {
    global $conn;
    
    // Check if cart is not empty
    if (empty($_SESSION['cart'])) {
        return false;
    }
    
    try {
        // Start transaction
        $conn->beginTransaction();
        
        // Insert order
        $stmt = $conn->prepare("INSERT INTO orders (user_id, total_price, address, payment_method) VALUES (?, ?, ?, ?)");
        $stmt->execute([$user_id, calculate_total(), $address, $payment_method]);
        $order_id = $conn->lastInsertId();
        
        // Insert order items
        foreach ($_SESSION['cart'] as $item) {
            $stmt = $conn->prepare("INSERT INTO order_items (order_id, product_id, quantity, price) VALUES (?, ?, ?, ?)");
            $stmt->execute([$order_id, $item['id'], $item['quantity'], $item['price']]);
        }
        
        // Clear cart
        $_SESSION['cart'] = [];
        
        // Commit transaction
        $conn->commit();
        return true;
    } catch (PDOException $e) {
        // Rollback transaction on error
        $conn->rollBack();
        echo "Error: " . $e->getMessage();
        return false;
    }
}

// Example usage:
if ($_SERVER['REQUEST_METHOD'] == 'POST') {
    if (isset($_POST['add_to_cart'])) {
        add_to_cart($_POST['product_id']);
    } elseif (isset($_POST['remove_from_cart'])) {
        remove_from_cart($_POST['product_id']);
    } elseif (isset($_POST['checkout'])) {
        $user_id = 1; // Get user ID from session or login
        $address = $_POST['address'];
        $payment_method = $_POST['payment_method'];
        checkout($user_id, $address, $payment_method);
    }
}
