if (isset($_GET['action']) && $_GET['action'] == 'remove') {
    $product_id = intval($_GET['product_id']);
    
    if (isset($_SESSION['cart'][$product_id])) {
        unset($_SESSION['cart'][$product_id]);
    }
}

