    foreach ($_POST['quantity'] as $item_id => $quantity) {
        if ($quantity < 1) {
            unset($cart[$item_id]);
        } else {
            $cart[$item_id]['quantity'] = $quantity;
        }
    }

    setcookie('cart', serialize($cart), time() + 3600);
}

// Calculate total
$total = 0;
