<?php
session_start();

// Check if session is not set
if (!isset($_SESSION['cart'])) {
    $_SESSION['cart'] = array();
}

// Add item to cart
function addToCart($item) {
    global $conn;
    
    // Item should have: id, name, price
    
    $sku = $item['id'];
    
    if (isset($_SESSION['cart'][$sku])) {
        // Update quantity
        $_SESSION['cart'][$sku]['quantity']++;
    } else {
        // Add new item
        $_SESSION['cart'][$sku] = array(
            'id' => $item['id'],
            'name' => $item['name'],
            'price' => $item['price'],
            'quantity' => 1
        );
    }
}

// Remove item from cart
function removeFromCart($sku) {
    if (isset($_SESSION['cart'][$sku])) {
        unset($_SESSION['cart'][$sku]);
    }
}

// Update quantity of an item
function updateQuantity($sku, $quantity) {
    if ($quantity > 0 && isset($_SESSION['cart'][$sku])) {
        $_SESSION['cart'][$sku]['quantity'] = $quantity;
    } else {
        removeFromCart($sku);
    }
}

// Display cart content
function displayCart() {
    $output = '';
    
    if (empty($_SESSION['cart'])) {
        return '<p>Your cart is empty.</p>';
    }
    
    $output .= '
        <table class="cart-table">
            <thead>
                <tr>
                    <th>Item</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Total</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>';
    
    $total = 0;
    
    foreach ($_SESSION['cart'] as $item) {
        $subtotal = $item['price'] * $item['quantity'];
        $total += $subtotal;
        
        $output .= '
            <tr>
                <td>' . $item['name'] . '</td>
                <td>$' . number_format($item['price'], 2) . '</td>
                <td>
                    <input type="number" 
                           value="' . $item['quantity'] . '" 
                           onChange="updateQuantity(' . $item['id'] . ', this.value)">
                </td>
                <td>$' . number_format($subtotal, 2) . '</td>
                <td><a href="#" onclick="removeFromCart(' . $item['id'] . ')">Remove</a></td>
            </tr>';
    }
    
    $output .= '
            </tbody>
        </table>
        
        <p>Grand Total: $' . number_format($total, 2) . '</p>
        <button onclick="checkout()">Checkout</button>';
    
    return $output;
}

