<?php
session_start();
require_once 'db.php';

// Initialize cart if it doesn't exist
if (!isset($_SESSION['cart'])) {
    $_SESSION['cart'] = array();
}

// Function to add item to cart
function addToCart($productId, $quantity = 1) {
    global $conn;
    
    // Check if product exists
    $stmt = $conn->prepare("SELECT * FROM products WHERE id = ?");
    $stmt->bind_param("i", $productId);
    $stmt->execute();
    $product = $stmt->get_result()->fetch_assoc();

    if ($product) {
        // Check if item is already in cart
        if (isset($_SESSION['cart'][$productId])) {
            // Update quantity
            $newQuantity = $_SESSION['cart'][$productId]['quantity'] + $quantity;
            $_SESSION['cart'][$productId]['quantity'] = $newQuantity;
        } else {
            // Add new item to cart
            $_SESSION['cart'][$productId] = array(
                'id' => $product['id'],
                'name' => $product['name'],
                'price' => $product['price'],
                'quantity' => $quantity,
                'image' => $product['image']
            );
        }
    } else {
        // Product not found
        return false;
    }

    return true;
}

// Function to update cart item quantity
function updateCartItem($productId, $newQuantity) {
    if (isset($_SESSION['cart'][$productId])) {
        $_SESSION['cart'][$productId]['quantity'] = $newQuantity;
        return true;
    }
    return false;
}

// Function to delete item from cart
function deleteCartItem($productId) {
    if (isset($_SESSION['cart'][$productId])) {
        unset($_SESSION['cart'][$productId]);
        return true;
    }
    return false;
}

// Function to calculate total price
function calculateTotal() {
    $total = 0;
    foreach ($_SESSION['cart'] as $item) {
        $total += ($item['price'] * $item['quantity']);
    }
    return $total;
}

// Function to display cart items
function displayCart() {
    if (empty($_SESSION['cart'])) {
        echo "<p>Your cart is empty.</p>";
        return;
    }

    foreach ($_SESSION['cart'] as $item) {
        echo "<div class='cart-item'>";
        echo "<img src='" . $item['image'] . "' alt='" . $item['name'] . "'>";
        echo "<h3>" . $item['name'] . "</h3>";
        echo "<p>Price: $" . number_format($item['price'], 2) . "</p>";
        echo "<input type='number' value='" . $item['quantity'] . "' min='1' max='99'>";
        echo "<button onclick='updateQuantity(" . $item['id'] . ")'>Update</button>";
        echo "<button onclick='deleteItem(" . $item['id'] . ")'>Delete</button>";
        echo "</div>";
    }
}

// Function to empty the cart
function emptyCart() {
    $_SESSION['cart'] = array();
}

// Example usage:
if (isset($_GET['action'])) {
    switch ($_GET['action']) {
        case 'add':
            if (!empty($_GET['id']) && !empty($_GET['quantity'])) {
                addToCart($_GET['id'], $_GET['quantity']);
            }
            break;
        case 'update':
            if (!empty($_GET['id'])) {
                updateCartItem($_GET['id'], $_GET['quantity']);
            }
            break;
        case 'delete':
            if (!empty($_GET['id'])) {
                deleteCartItem($_GET['id']);
            }
            break;
    }
}

// Display cart contents
displayCart();
