class Product {
    private $id;
    private $name;
    private $price;
    private $description;
    private $image;

    public function __construct($id, $name, $price, $description = '', $image = '') {
        $this->id = $id;
        $this->name = $name;
        $this->price = $price;
        $this->description = $description;
        $this->image = $image;
    }

    // Getters
    public function getId() { return $this->id; }
    public function getName() { return $this->name; }
    public function getPrice() { return $this->price; }
    public function getDescription() { return $this->description; }
    public function getImage() { return $this->image; }
}


class Cart {
    private $items;
    private $total;

    public function __construct($items = array()) {
        $this->items = $items;
        $this->calculateTotal();
    }

    public function addItem($productId, $quantity = 1) {
        if (isset($this->items[$productId])) {
            $this->items[$productId]['quantity'] += $quantity;
        } else {
            $this->items[$productId] = array(
                'product_id' => $productId,
                'quantity' => $quantity
            );
        }
        $this->calculateTotal();
    }

    public function removeItem($productId) {
        unset($this->items[$productId]);
        $this->calculateTotal();
    }

    public function updateQuantity($productId, $newQuantity) {
        if (isset($this->items[$productId])) {
            if ($newQuantity > 0) {
                $this->items[$productId]['quantity'] = $newQuantity;
            } else {
                unset($this->items[$productId]);
            }
            $this->calculateTotal();
        }
    }

    public function getItems() {
        return $this->items;
    }

    private function calculateTotal() {
        $total = 0;
        foreach ($this->items as $item) {
            // Here you would fetch the product price from your database
            // For this example, we're assuming a fixed price of 10.00 per item
            $price = 10.00; 
            $total += ($item['quantity'] * $price);
        }
        $this->total = $total;
    }

    public function getTotal() {
        return $this->total;
    }
}


