<?php
class ShoppingCart {
    private $db;

    public function __construct($db) {
        $this->db = $db;
    }

    // Add item to cart
    public function addItem($productId, $quantity = 1) {
        if (!isset($_SESSION)) {
            session_start();
        }
        
        $userId = $_SESSION['user_id'] ?? null;

        try {
            // Check if item already exists in cart
            $stmt = $this->db->prepare("
                SELECT id FROM shopping_cart 
                WHERE product_id = ? AND user_id = ?
            ");
            $stmt->execute([$productId, $userId]);
            $existingItem = $stmt->fetch(PDO::FETCH_ASSOC);

            if ($existingItem) {
                // Update quantity
                $this->updateItemQuantity($existingItem['id'], $quantity);
                return true;
            } else {
                // Insert new item
                $stmt = $this->db->prepare("
                    INSERT INTO shopping_cart (product_id, quantity, user_id)
                    VALUES (?, ?, ?)
                ");
                $result = $stmt->execute([$productId, $quantity, $userId]);
                return $result;
            }
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // View cart items
    public function viewCart() {
        if (!isset($_SESSION)) {
            session_start();
        }
        
        $userId = $_SESSION['user_id'] ?? null;

        try {
            $stmt = $this->db->prepare("
                SELECT sc.id, p.name, p.description, p.price, 
                       sc.quantity, sc.added_at
                FROM shopping_cart sc
                JOIN products p ON sc.product_id = p.id
                WHERE sc.user_id = ?
            ");
            $stmt->execute([$userId]);
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Update item quantity
    public function updateItemQuantity($cartItemId, $quantity) {
        try {
            if ($quantity <= 0) {
                $this->removeItem($cartItemId);
                return true;
            }

            $stmt = $this->db->prepare("
                UPDATE shopping_cart 
                SET quantity = ?, updated_at = CURRENT_TIMESTAMP
                WHERE id = ?
            ");
            $result = $stmt->execute([$quantity, $cartItemId]);
            return $result;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Remove item from cart
    public function removeItem($cartItemId) {
        try {
            $stmt = $this->db->prepare("
                DELETE FROM shopping_cart 
                WHERE id = ?
            ");
            $result = $stmt->execute([$cartItemId]);
            return $result;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }
}


