    "strawberry shortcake" => "Classic strawberry shortcake recipe",
    "blueberry muffin" => "Perfect blueberry muffin recipe",
  ];

  $results = [];
  foreach ($products as $key => $value) {
    if (stripos($key, $search_term) !== false) { // Case-insensitive search
      $results[] = [
        "name" => $key,
        "description" => $value,
