  $searchTerm = $db->real_escape_string($searchTerm);

  // Build the SQL query
  $sql = "SELECT * FROM your_table_name WHERE column_name LIKE '%" . $searchTerm . "%'";

  // Execute the query
  $result = $db->query($sql);

  // Prepare the results
  $results = array();
  if ($result->num_rows > 0) {
    while($row = $result->fetch_assoc()) {
      $results[] = $row;
    }
  }

  return $results;
}

