    // Handle the error
    echo "Error preparing statement: " . $db->error;
    exit;
}

// Bind parameters
foreach ($columns as $column) {
  $stmt->bind_param($column, "%$searchTerm%"); // Use % to escape the variable
}

// Execute the query
$stmt->execute();

// Get results
$results = $stmt->get_result();

// Close statement
$stmt->close();


