// Function to handle the search
function searchProducts($searchTerm, $products) {
    $results = [];

    foreach ($products as $key => $value) {
        if (strtolower($key) == strtolower($searchTerm)) {
            $results[$key] = $value;
        } elseif (strpos($value, strtolower($searchTerm)) !== false) {
            $results[$key] = $value;
        }
    }

    return $results;
}

