    ['id' => 3, 'name' => 'Orange', 'description' => 'A citrus fruit'],
    ['id' => 4, 'name' => 'Grape', 'description' => 'Small, sweet fruit']
  ];


  $results = [];

  foreach ($data as $row) {
    // Perform case-insensitive search
    if (stripos($row[$column_to_search], $search_term) !== false) {
      $results[] = $row;
    }
  }

  return $results;
}


