            "Example Result 1",
            "Example Result 2",
            "Example Result 3"
        );

        if (!empty($results)) {
            foreach ($results as $result) {
                echo "<div style='padding: 5px; border-bottom: 1px solid #ddd;'>" . $result . "</div>";
            }
        } else {
            echo "<p>No results found for your search.</p>";
        }
